    private void jButton16ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton16ActionPerformed
     n2=Float.parseFloat(Escribir.getText());
     switch(operacion){
     case "+":
         Escribir.setText(Float.toString(n1+n2));
         break;
         
     case "-":
         Escribir.setText(Float.toString(n1-n2));
         break;
     case "/":
         if(n2==0){
              Escribir.setText("No existe divisiÃ³n por cero. ERROR");
              break;
         }
         Escribir.setText(Float.toString(n1/n2));
         break;
    case "*":
         Escribir.setText(Float.toString(n1*n2));
         break;
    case "âˆš":
         if(n2<0 && n1%2==0){
              Escribir.setText("Indefinido calcular raiz del negativo");
              break;
         }
         if (n2<0){
             for (int i=1;i<n1;i++){
             aux=aux*(1/n2);    
         }
            Escribir.setText(Float.toString((aux)));
             break;
         }
         else {
              Escribir.setText(Float.toString((float) Math.pow(n2, 1/n1)));
         break;
         }
        
    case "^":
         Escribir.setText(Float.toString((float) Math.pow(n1, n2)));
         break;
    case "%":
         Escribir.setText(Float.toString(n1*n2/100));
         break;
    case "sin":
         Escribir.setText(Float.toString((float)Math.sin(Math.toRadians(n2))));
         break;
    case "cos":
         Escribir.setText(Float.toString((float)Math.cos(Math.toRadians(n2))));
         break;
    case "tan":
         if(n2==90){
              Escribir.setText("El resultado es indeterminado");
              break;
         }
         Escribir.setText(Float.toString((float)Math.tan(Math.toRadians(n2))));
         break;
         }  
    }//GEN-LAST:event_jButton16ActionPerformed

